import React, {useState} from "react";
import searchButton from "../imgs/search-button.svg";
import tempIcon from "../imgs/suny.svg";
import Humidity from "../imgs/humidity.png";
import Wind from "../imgs/wind.png";


const api ={
    key: "844ef5767a95cfb4fbdfaf2fa243b192",
    base: "https://api.openweathermap.org/data/2.5/"
}

const Search = () => {

    const [search, setSearch] = useState();
    const [weather, setWeather] = useState();

    const searchProcessed = () => {
    fetch(`${api.base}weather?q=${search}&units=metric&APPID=${api.key}`)
    .then((res) => res.json())
    .then((result) =>{
        setWeather(result);
    });
    };

  return (
    <>
    <div className="search">
      <div className="searchInput">
        <input type="search" name="" id="" placeholder="Search" onChange={(e) => setSearch(e.target.value)} />
      </div>
      <div className="searchButton">
        <button type="submit" onClick={searchProcessed}>
          <img src={searchButton} />
          
        </button>
      </div>
    </div>
    <div className="weatherCard">
    <div className="card-wrap">
      <div className="cityName">
        {/* <h1>{weather.name}</h1> */}
      </div>
      <div className="tempIcon">
        <img src={tempIcon} alt="icon" />
      </div>
      <div className="cityTemp">
        <h1>{weather.main.temp}</h1>
      </div>
      <div className="detail">
        <div className="tempDetail">
          <div className="detailImage">
            <img src={Humidity} alt="" srcset="" />
          </div>
          <div className="detailContent">
            <div className="detailValue">
              <h5>{weather.main.humidity}</h5>
            </div>
            <detail className="detailName">
              <h5>Humidity</h5>
            </detail>
          </div>
        </div>
        <div className="tempDetail">
          <div className="detailImage">
            <img src={Wind} alt="" srcset="" />
          </div>
          <div className="detailContent">
            <div className="detailValue">
              <h5 id="noSpace">2km/hr</h5>
            </div>
            <detail className="detailName">
              <h5>{weather.main.pressure}</h5>
            </detail>
          </div>
        </div>
      </div>
    </div>
  </div>
  </>
  );
};

export default Search;
